import React, { useState } from "react";

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' },
  ]);
  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [st, setNewFilter] = useState('');

  const setName = (event) => {
    setNewName(event.target.value);
  };

  const setNumber = (event) => {
    setNewNumber(event.target.value);
  };

  const setFilter = (event) => {
    setNewFilter(event.target.value);
  };

  const numbers = () => {persons.filter(person => person.name.includes(st))}

  const addContact = (event) => {
    event.preventDefault();
    if(!persons.includes({name: newName})) {
      const newPerson = {
        name: newName,
        number: newNumber,
      };

      setPersons(persons.concat(newPerson));
      setNewName("");
      setNewNumber("");
    }
    else {
      window.alert(`${newName} is already added to phonebook`)
    }
  };

  const Person = (props) => {
    return (
      <p>{props.name} {props.number}</p>
    )
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <p>filter shown with <input value={st} onChange={setFilter} /></p>
      <h2>Add a new</h2>
      <form onSubmit={addContact}>
        <div>name: <input value={newName} onChange={setName} /></div>
        <div>number: <input value={newNumber} onChange={setNumber}/></div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {numbers.map((person) =>
        <Person key={person.name} name={person.name} number={person.number}/>
      )}
    </div>
  );
};

export default App;
