import React from 'react'

const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
}

const Part = (props) => {
  return (
    <p>
        {props.part.name} {props.part.exercises}
    </p>
  )
}

const Content = (props) => {
  const array = []
  let i = 0
  props.parts.forEach(element => {
    array.push(<Part part={props.parts[i]} />)
  });
  return array
}

const Total = (props) => {
  let total = 0
  props.parts.forEach(element => {
    total = total + element;
  });
  return (
    <p>Number of exercises {}</p>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
  {
    name: 'Fundamentals of React',
    exercises: 10
  },
  {
    name: 'Using props to pass data',
    exercises: 7
  },
  {
    name: 'State of a component',
    exercises: 14
  }
]

  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  )
}

export default App
